(34, 'bbpos-chipper-launch', 'marketing', 'en_US', 'Ready to grow your business beyond online?', 'Collect payment in person for orders placed online using WooCommerce In-Person Payments. The BBPOS Chipper card reader can process payments securely, and keep everything synced in your WooCommerce Payments dashboard  no matter where you are.', '{}', 'pending', 'woocommerce.com', '2021-11-04 21:45:09', NULL, 0, 'plain', '', 0, 'info'),
(35, 'habit-moment-survey', 'marketing', 'en_US', 'We\'re all ears! Share your experience so far with WooCommerce', 'We\'d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2021-11-04 21:45:09', NULL, 0, 'plain', '', 0, 'info'),
(36, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2021-11-04 21:45:10', NULL, 0, 'plain', '', 0, 'info'),
